frappe.listview_settings['Payment Request']["onload"] = function (listview){ 
    cur_list.page.add_button("Noneyplexexport gebuchter Einträge", () => {
        frappe.confirm("This operation will set the 'Für Moneyplex exportiert' field to 1, for all Payment Request that are submitted and were not exported yet. Are you sure you want to continue? ", ()=> {
            frappe.call({
                method: "moneyplex.moneyplex.server_scripts.moneyplex_export"
            }).then( r => {
                downloadFileFromURL(r.message , `moneyplex_export_${getCurrentDateFormatted()}.xlsx`);
            })
        })
    })
}

function downloadFileFromURL(url, fileName) {
  fetch(url)
    .then(response => response.blob())
    .then(blob => {
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = fileName;
      link.click();
    })
    .catch(error => console.error('Error downloading file:', error));
}

function getCurrentDateFormatted() {
  const now = new Date();

  const year = now.getFullYear();
  const month = String(now.getMonth() + 1).padStart(2, '0'); // Add 1 because months are zero-based
  const day = String(now.getDate()).padStart(2, '0');

  const formattedDate = `${year}${month}${day}`;

  return formattedDate;
}
